import React, { Component } from 'react'
import Movies from '../Movies/Movies';

class MoreMovies extends Component {
    constructor(){
        super()
        this.state = {
            cantidad: 0
        }
    }

    agregarMas(url) {
        fetch(url)
            .then( respuesta => respuesta.json() )
            .then(data => {
                console.log(data);
                this.setState({
                    peliculas: data.results,
                })
            })
            .catch( err => console.log(err))
    }

    render(){
        return (
            <>
            <button onClick={()=>this.agregarMas('https://api.themoviedb.org/3/movie/popular?api_key=c40a745984e9eb09a7b68f074f0aa025?index=' + this.state.cantidad + this.state.peliculas.length + "&limit=20")}>Mas Peliculas</button>
            </>
        );
    }
}

export default MoreMovies
