            <>
            <div className="cards">
                {this.state.peliculas.map( (pelicula) => (
                    <Card key={pelicula.id} datosPelicula={pelicula} />
                ))}
            </div>
            <button onClick={()=>this.agregarMas()}>Mas Peliculas</button>
            </>
        );
    }

}

export default Movies
